  // Regex to match unescaped double quotes and backslashes
  const regex = /(?:\\[\\"]|[^\\"])*"|\\./g;

  // Replace callback function
  function replacer(match) {
    if (match === '\\') {
      return '\\\\';
    }
    if (match === '"') {
      return '\\"';
    }
    return match;
  }

  // Preprocess the JSON string
